opam.opam:
opam-version: "1.2"
maintainer: "opensource@janestreet.com"
authors: "Jane Street Group, LLC <opensource@janestreet.com>"
license: "Apache-2.0"
homepage: "https://github.com/ocaml-ppx/ppxlib"
bug-reports: "https://github.com/ocaml-ppx/ppxlib/issues"
depends: [
  "base" {>= "v0.11.0"}
  "dune" {build}
  "ocaml-compiler-libs" {>= "v0.11.0"}
  "ocaml-migrate-parsetree" {>= "1.0.9"}
  "ppx_derivers" {>= "1.0"}
  "stdio" {>= "v0.11.0"}
]
available: ocaml-version >= "4.04.1"
build: ["jbuilder" "build" "-p" name "-j" jobs]
dev-repo: "git+https://github.com/ocaml-ppx/ppxlib.git"

opam-version: "1.2"
maintainer: "opensource@janestreet.com"
authors: "Jane Street Group, LLC <opensource@janestreet.com>"
license: "Apache-2.0"
homepage: "https://github.com/janestreet/ppx_let"
bug-reports: "https://github.com/janestreet/ppx_let/issues"
depends: [
  "base" {>= "v0.11" & < "v0.12"}
  "jbuilder" {build & >= "1.0+beta18.1"}
  "ocaml-migrate-parsetree" {>= "1.0"}
  "ppxlib" {>= "0.1.0"}
]
conflicts: [
  "jbuilder" {= "1.0+beta19"}
]
available: ocaml-version >= "4.04.1"
build: ["jbuilder" "build" "-p" name "-j" jobs]
dev-repo: "git+https://github.com/janestreet/ppx_let.git"

opam-version: "1.2"
version: "4.1.0"
maintainer: [
  "Anton Bachin <antonbachin@yahoo.com>"
  "Mauricio Fernandez <mfp@acm.org>"
  "Simon Cruanes <simon.cruanes.2007@m4x.org>"
]
authors: ["Jérôme Vouillon" "Jérémie Dimino"]
license: "LGPL with OpenSSL linking exception"
homepage: "https://github.com/ocsigen/lwt"
doc: "https://ocsigen.org/lwt/manual/"
bug-reports: "https://github.com/ocsigen/lwt/issues"
depends: [
  "cppo" {build & >= "1.1.0"}
  "jbuilder" {build & >= "1.0+beta14"}
  "ocamlfind" {build & >= "1.7.3-1"}
  "result"
]
depopts: ["base-threads" "base-unix" "conf-libev"]
available: ocaml-version >= "4.02.0" & compiler != "4.02.1+BER"
build: [
  ["ocaml" "src/util/configure.ml" "-use-libev" "%{conf-libev:installed}%"]
  ["jbuilder" "build" "-p" name "-j" jobs]
]
messages: [
  "For the PPX, please install package lwt_ppx" {!lwt_ppx:installed}
  "For the Camlp4 syntax, please install package lwt_camlp4"
    {camlp4:installed & !lwt_camlp4:installed}
  "For Lwt_log and Lwt_daemon, please install package lwt_log"
    {!lwt_log:installed}
]
dev-repo: "git+https://github.com/ocsigen/lwt.git"

opam-version: "1.2"
maintainer: "unixjunkie@sdf.org"
authors: "Carlo Wood"
license: "GPL-2+"
homepage: "http://www.gnu.org/software/which/"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["which" "which"]
depexts: [
  ["which"] {"centos"}
  ["which"] {"fedora"}
  ["which"] {"opensuse"}
  ["debianutils"] {"debian"}
  ["debianutils"] {"ubuntu"}
  ["which"] {"nixpkgs"}
  ["which"] {"archlinux"}
]
dev-repo: "git+https://github.com/ocaml/opam-repository.git"

opam-version: "1.2"
maintainer: "tim@gfxmonk.net"
license: "GPL-3"
homepage: "http://www.gnu.org/software/m4/m4.html"
bug-reports: "https://github.com/ocaml/opam-repository/issues"
build: ["sh" "-exc" "echo | m4"]
depexts: [
  ["m4"] {"debian"}
  ["m4"] {"ubuntu"}
  ["m4"] {"fedora"}
  ["m4"] {"rhel"}
  ["m4"] {"centos"}
  ["m4"] {"alpine"}
  ["m4"] {"nixpkgs"}
  ["m4"] {"opensuse"}
  ["m4"] {"oraclelinux"}
  ["m4"] {"archlinux"}
]
dev-repo: "git+https://github.com/ocaml/opam-repository.git"
